const buttonList=document.getElementById("button-list"),btnBack=document.getElementById("back-btn"),allShow=document.getElementsByClassName("show"),projectList=document.getElementsByClassName("project");for(let t=0;t<projectList.length;t++){let e=document.createElement("button");e.id=`btn-${t+1}`,e.classList.add("project-btn"),e.innerText=`project ${t+1}`,buttonList.appendChild(e)}const projectButtons=document.getElementsByClassName("project-btn");for(let t=0;t<projectButtons.length;t++)projectButtons[t].addEventListener("click",(()=>{let e=document.getElementById(`project-${t+1}`);e.classList.remove("hide"),e.classList.add("show")}));btnBack.addEventListener("click",(()=>{for(let t=0;t<allShow.length;t++)allShow[t].classList.add("hide"),allShow[t].classList.remove("show")}));