const buttonList=document.getElementById("button-list"),btnBack=document.getElementById("back-btn"),allShow=document.getElementsByClassName("show"),projectList=document.getElementsByClassName("project"),createButton=document.getElementById("create"),createSection=document.getElementById("create-project"),settingSection=document.getElementById("settings-menu"),hamburgerIcon=document.getElementById("hamburger-icon"),handedSelect=document.getElementById("which_handed"),controls=document.getElementsByClassName("controls");for(let e=0;e<projectList.length;e++){let t=document.createElement("button");t.id=`btn-${e+1}`,t.classList.add("project-btn"),t.innerText=`project ${e+1}`,buttonList.appendChild(t)}const projectButtons=document.getElementsByClassName("project-btn");for(let e=0;e<projectButtons.length;e++)projectButtons[e].addEventListener("click",(()=>{let t=document.getElementById(`project-${e+1}`);t.classList.remove("hide"),t.classList.add("show")}));btnBack.addEventListener("click",(()=>{for(let e=0;e<allShow.length;e++)allShow[e].classList.add("hide"),allShow[e].classList.remove("show")})),createButton.addEventListener("click",(()=>{createSection.classList.remove("hide"),createSection.classList.add("show")})),hamburgerIcon.addEventListener("click",(()=>{settingSection.classList.contains("show")?(settingSection.classList.remove("show"),settingSection.classList.add("hide")):(settingSection.classList.remove("hide"),settingSection.classList.add("show"))})),handedSelect.addEventListener("change",(()=>{if("right"==handedSelect.value)for(let e=0;e<controls.length;e++)controls[e].classList.add("right-handed"),controls[e].classList.contains("left-handed")&&controls[e].classList.remove("left-handed");else if("left"==handedSelect.value)for(let e=0;e<controls.length;e++)controls[e].classList.add("left-handed"),controls[e].classList.contains("right-handed")&&controls[e].classList.remove("right-handed")}));